import React, { useState, useEffect } from 'react';
import defaultMenuData from './resources/menu.json';

function App() {
  // 1. State for the menu data
  const [menuData, setMenuData] = useState(() => {
    const savedData = localStorage.getItem('menuData');
    return savedData ? JSON.parse(savedData) : defaultMenuData;
  });

  // 2. State for showing/hiding the "Add New Item" modal form
  const [showForm, setShowForm] = useState(false);

  // 3. State for the new item being created
  const [newItem, setNewItem] = useState({
    name: '',
    description: '',
    price: '',
    category: '',
    preparation_time: ''
  });

  // 4. Error messages for form validation
  const [errors, setErrors] = useState({});

  // 5. Persist any changes to localStorage
  useEffect(() => {
    localStorage.setItem('menuData', JSON.stringify(menuData));
  }, [menuData]);

  // 6. Handle item deletion
  const handleDelete = (indexToRemove) => {
    const newMenu = [...menuData.menu];
    newMenu.splice(indexToRemove, 1);
    setMenuData({ menu: newMenu });
  };

  // 7. Validation function for the form
  const validateForm = () => {
    const newErrors = {};

    if (!newItem.name.trim()) {
      newErrors.name = 'אנא הזן שם מנה';
    }
    if (!newItem.description.trim()) {
      newErrors.description = 'אנא הזן תיאור';
    }
    if (!newItem.price.trim()) {
      newErrors.price = 'אנא הזן מחיר';
    } else if (isNaN(Number(newItem.price))) {
      newErrors.price = 'מחיר חייב להיות מספרי';
    }
    if (!newItem.category.trim()) {
      newErrors.category = 'אנא הזן קטגוריה';
    }
    if (!newItem.preparation_time.trim()) {
      newErrors.preparation_time = 'אנא הזן זמן הכנה';
    } else if (isNaN(Number(newItem.preparation_time))) {
      newErrors.preparation_time = 'זמן הכנה חייב להיות מספרי';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0; // returns true if no errors
  };

  // 8. Handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();
    if (!validateForm()) return; // stop if there are validation errors

    // Create a new item object with properly typed fields
    const itemToAdd = {
      name: newItem.name.trim(),
      description: newItem.description.trim(),
      price: parseFloat(newItem.price),
      category: newItem.category.trim(),
      preparation_time: parseInt(newItem.preparation_time, 10)
    };

    // Update state
    const updatedMenu = [...menuData.menu, itemToAdd];
    setMenuData({ menu: updatedMenu });

    // Reset the form fields and hide the form
    setNewItem({
      name: '',
      description: '',
      price: '',
      category: '',
      preparation_time: ''
    });
    setShowForm(false);
    setErrors({});
  };

  // 9. Handle changes in form inputs
  const handleChange = (e) => {
    const { name, value } = e.target;
    setNewItem((prev) => ({ ...prev, [name]: value }));
  };

  // Basic inline styling for a simple “modal”
  const modalStyle = {
    position: 'fixed',
    top: 0,
    left: 0,
    width: '100%',
    height: '100%',
    backgroundColor: 'rgba(0, 0, 0, 0.5)',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center'
  };

  const modalContentStyle = {
    backgroundColor: '#fff',
    padding: '20px',
    borderRadius: '5px',
    width: '300px'
  };

  return (
    // Keep the RTL direction for Hebrew text
    <div dir="rtl" style={{ margin: '20px', textAlign: 'right' }}>
      <h2>תפריט בעברית (RTL)</h2>

      {/* 10. Add New Item button */}
      <button onClick={() => setShowForm(true)}>
        הוסף פריט חדש
      </button>

      {/* 11. The table */}
      <table border="1" cellPadding="8" cellSpacing="0" style={{ marginTop: '10px' }}>
        <thead>
          <tr>
            <th>שם</th>
            <th>מחיר</th>
            <th>מחיקה</th>
          </tr>
        </thead>
        <tbody>
          {menuData.menu.map((item, index) => (
            <tr key={index}>
              <td>{item.name}</td>
              <td>{item.price}</td>
              <td>
                <button onClick={() => handleDelete(index)}>מחק</button>
              </td>
            </tr>
          ))}
          {menuData.menu.length === 0 && (
            <tr>
              <td colSpan="3">אין פריטים</td>
            </tr>
          )}
        </tbody>
      </table>

      {/* 12. Popup form (conditional rendering) */}
      {showForm && (
        <div style={modalStyle}>
          <div style={modalContentStyle}>
            <h3>הוסף פריט חדש</h3>
            <form onSubmit={handleSubmit}>
              <div>
                <label>שם:</label>
                <input
                  name="name"
                  value={newItem.name}
                  onChange={handleChange}
                />
                {errors.name && <div style={{ color: 'red' }}>{errors.name}</div>}
              </div>
              <div>
                <label>תיאור:</label>
                <input
                  name="description"
                  value={newItem.description}
                  onChange={handleChange}
                />
                {errors.description && <div style={{ color: 'red' }}>{errors.description}</div>}
              </div>
              <div>
                <label>מחיר:</label>
                <input
                  name="price"
                  value={newItem.price}
                  onChange={handleChange}
                />
                {errors.price && <div style={{ color: 'red' }}>{errors.price}</div>}
              </div>
              <div>
                <label>קטגוריה:</label>
                <input
                  name="category"
                  value={newItem.category}
                  onChange={handleChange}
                />
                {errors.category && <div style={{ color: 'red' }}>{errors.category}</div>}
              </div>
              <div>
                <label>זמן הכנה (בדקות):</label>
                <input
                  name="preparation_time"
                  value={newItem.preparation_time}
                  onChange={handleChange}
                />
                {errors.preparation_time && (
                  <div style={{ color: 'red' }}>{errors.preparation_time}</div>
                )}
              </div>

              <div style={{ marginTop: '10px' }}>
                <button type="submit">שמור</button>
                <button type="button" onClick={() => setShowForm(false)}>
                  ביטול
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
